import React from "react";
import PropTypes from 'prop-types';
import Moment from 'react-moment';


const Education = ({ education }) => {

    const educations= education.map(education => (

        <tr key="education.id">
            <td>{education.school}</td>
            <td className="hide-sm">{education.degree}</td>
            <td>
                <Moment format='YYYY/MM/DD'>{education.from}</Moment> -{' '}
                {
                education.to === null ? 
                ('Now') :
                
                (<Moment format='YYYY/MM/DD'>{education.to}</Moment>)}
            </td>
            <td>
                
             <i className="fas fa-trash-alt text-danger"></i> Delete
             </td>
        </tr>
    ))

    return(

    <React.Fragment>
        
     <h2 className="my-5">Education</h2>
     <table className="table table-bordered table-hover table-striped">
         <thead>
             <tr>
                 <th className="bg-info">School</th>
                 <th className="hide-sm bg-info">Degree</th>
                 <th className="hide-sm bg-info">Years</th>
                 <th >Delete</th>
             </tr>
         </thead>

         <tbody>
             { educations }
         </tbody>
     </table>
    </React.Fragment>

    )
}

Education.propTypes = {

    education: PropTypes.array.isRequired
}

export default Education;
