import axios from "axios";
import { setAlert } from "./alertAction";
import {  GET_PROFILE, PROFILE_ERROR, UPDATE_PROFILE } from "./types";

//Get current users profile
export const getProfile = () => async dispatch => {

    try {

        const res = await axios.get('/api/profile/me');

        dispatch({

            type: GET_PROFILE,
            payload: res.data
        })
        
    } catch (err) {

        dispatch({

            type: PROFILE_ERROR,
            payload: {msg: err.response.data.message, status: err.response.status}
        })
        
    }
}


// Create or update user Profile
export const createProfile = (formData, history, edit = false) => async dispatch => {

    try {

        const config = {

            headers: {

                'Content-Type': 'application/json'
            }
        }

       const body = JSON.stringify(formData)

       const res = await axios.post('/api/profile', body, config);

       dispatch({

        type: GET_PROFILE,
        payload: res.data
       })

       dispatch(setAlert(edit ? 'Your Profile has been updated successfully' : 'Your Profile has been created successfully', 'success'));

       if(!edit) {

       history.push('/dashboard');
       }
        
    } catch (err) {

        const errors = err.response.data.errors;

        if(errors) {

            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({

            type: PROFILE_ERROR,
            payload: { msg: err.response.data.message, status: err.response.status }
        })
        
    }
}


// Add Experience
export const addExperience = (formData, history) => async dispatch => {

    try {

        const config = {

            headers: {

                'Content-Type': 'application/json'
            }
        };

        const body = JSON.stringify(formData);

        const res = await axios.put('/api/profile/experience', body, config);

        dispatch({

            type: UPDATE_PROFILE,
            payload: res.data
        })

        dispatch(setAlert('Your Experience added successfully', 'success'));

        history.push('/dashboard');


        
    } catch (err) {

        const errors = err.response.data.errors;

        if(errors) {

            errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));
        }

        dispatch({

            type: PROFILE_ERROR,
            payload: { msg: err.response.data.message, status: err.response.status }
        })
        
    }
}


// Add Education
export const addEducation = (formData, history) => async dispatch => {

    try {

        const config = {

            headers: {

                'Content-Type': 'application/json'
            }
        }

        const body = JSON.stringify(formData);

        const res = await axios.post('/api/profile/education', body, config);
        
    } catch (err) {

        const errors = err.response.data.errors;

        errors.forEach(error => dispatch(setAlert(error.msg, 'danger')));

        dispatch({

            type: PROFILE_ERROR,
            payload: { msg: err.response.data.message, status: err.response.status }
        })
        
    }
}
