import React, { Fragment, useEffect } from 'react'
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../Layout/Spinner';
import { getProfileById } from '../../actions/profileAction';
import ProfileTop from './ProfileTop';

const Profile = ({ getProfileById, match, profile: {profile, loading}, auth }) => {

    useEffect(() => {

        getProfileById(match.params.id);

    }, [getProfileById, match.params.id]);


return(

    <React.Fragment>
        {profile === null || loading ? <Spinner/> : (<React.Fragment>
           
         <Link to='/profiles' className="btn btn-primary"> 
         
         
         Back to Profiles 
         
         
         </Link>  

         <br/>

         {auth.isAuth &&
          auth.loading === false &&
           auth.user._id === profile.user._id && 
           (<Link to='/edit-profile' className="btn btn-primary">Edit Profile</Link>)}
            
            <div className="profile-grid my-3">
            <ProfileTop profile={ profile }/>
            </div>   
       

        </React.Fragment>)}
    </React.Fragment>
)

    // return profile ? (


    //     <React.Fragment>
                     
    //               

    //                </React.Fragment>

    // ) : null;
       

 }
     

Profile.propTypes = {

    getProfileById: PropTypes.func.isRequired,
    profile: PropTypes.object.isRequired,
    auth: PropTypes.object.isRequired
}

const mapStateToProps = state => ({

    profile: state.profile,
    auth: state.auth
})

    


export default connect(mapStateToProps, { getProfileById }) (Profile);
